<?phpClass Vendor_model extends MY_Model{     function __construct(){        parent::__construct();        $this->has_one['region'] = array('foreign_model'=>'Region_model','foreign_table'=>'region','foreign_key'=>'id','local_key'=>'region');        $this->has_one['business_category'] = array('foreign_model'=>'Vendor_business_category_model','foreign_table'=>'vendor_category','foreign_key'=>'id','local_key'=>'business_catgory');        $this->has_many['users'] = array('foreign_model'=>'Vendor_user_model','foreign_table'=>'vendor_users','foreign_key'=>'vendor_id','local_key'=>'id');    }    public $table = "vendor";    public $primary_key = "id";        function logged_in(){        $vendor_id = $this->session->userdata('vendor_id');        if(!empty($vendor_id)){            return true;        }else{            return false;        }    }        /**     * logged_in     *     * @return integer     * @author jeevan     * */    public function get_vendor_id() {        $vendor_id = $this->session->userdata('vendor_id');        if (!empty($vendor_id)) {            return $vendor_id;        }        return null;    }    }